function p._main(frame) -- this cleans up outside (or INside) calls for args local a a = utils.tableShallowCopy(getargs(frame)) if #a == 0 then return false end glbls.args = a return true end
function p.main(frame) local retval = 'invalid call to BlankCommons-main: ' local a,sorc,mKey,mItem local ok,tempStr,ok2 local blnkItem = {} if not p._main(frame) then retval = retval..'zero arguments passed' return retval end
a = glbls.args sorc = a[1] mKey = a[2] mItem = a[3]
ok,tempStr = checkSorC(sorc) if ok then glbls.sorc = tempStr glbls.args[1] = tempStr -- bypass REchecking later... else return 'invalid sorc-value >>'..tostring(sorc)..'<<' end
--ok = utils.isSimple(mKey) ok = mKey and not utils.isSimpleBlank(mKey) if ok then ok2,tempStr = utils.isWikiLink(mKey) if ok2 then glbls.key = tempStr else glbls.key = mKey end if glbls.sorc == 'c' then blnkItem = p.crew() elseif glbls.sorc == 's' then blnkItem = p.ship() else end end
retval = utils.dumpTable(glbls) retval = retval..'\n\n'..utils.dumpTable(blnkItem) return retval
end
function p.ship() return STARSHIPBLANK end function p.crew() return CHARACTERBLANK end
return p

